import React, { useState } from 'react';

export default function Login({ onLogin }) {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [isDemo, setIsDemo] = useState(true);

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin({ ...credentials, accountType: isDemo ? 'demo' : 'live' });
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center">
      <div className="bg-gray-800 p-8 rounded-lg w-96">
        <h1 className="text-3xl font-bold text-white mb-6 text-center">Brightbot</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            value={credentials.email}
            onChange={(e) => setCredentials({...credentials, email: e.target.value})}
            className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={credentials.password}
            onChange={(e) => setCredentials({...credentials, password: e.target.value})}
            className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600"
            required
          />
          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              id="demo"
              checked={isDemo}
              onChange={(e) => setIsDemo(e.target.checked)}
            />
            <label htmlFor="demo" className="text-white">Demo Account</label>
          </div>
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white p-3 rounded font-medium"
          >
            Login to Brightbot
          </button>
        </form>
      </div>
    </div>
  );
}
